mapper.insert:
insert into js_quesion( type_id, title, an_A, an_B, an_C, an_D, answer,
reserve_five, reserve_six
) values
(
#{item.typeId,jdbcType=INTEGER}, #{item.title,jdbcType=VARCHAR}, #{item.anA,jdbcType=VARCHAR}, #{item.anB,jdbcType=VARCHAR}, #{item.anC,jdbcType=VARCHAR}, #{item.anD,jdbcType=VARCHAR}, #{item.answer,jdbcType=VARCHAR}, #{item.reserveFive,jdbcType=VARCHAR}, #{item.reserveSix,jdbcType=VARCHAR}
)

mapper.select:
  	SELECT
		js.id 'id',
		js.type_id 'typeId',
		js.title 'title',
		js.an_A,
		js.an_B,
		js.an_C,
		js.an_D,
		js.answer 'answer'
	FROM
		js_quesion js


	         type_id = #{typeId}


	        AND leibie_type = #{leibieType}


	        AND exam_type = #{exam_type}


	        AND difficult_type = #{difficult_type}


	        AND know_type = #{know_type}


	        AND reserve_five = #{reserveFive}


	        AND reserve_six = #{reserveSix}


	limit #{start},#{size}

  	SELECT
		js.id 'id',
		js.type_id 'typeId',
		js.title 'title',
		js.an_A,
		js.an_B,
		js.an_C,
		js.an_D,
		js.answer 'answer'
	FROM
		js_quesion js


	         type_id = #{typeId}


	        AND leibie_type = #{leibieType}


	        AND exam_type = #{exam_type}


	        AND difficult_type = #{difficult_type}


	        AND know_type = #{know_type}


	        AND reserve_five = #{reserveFive}


	        AND reserve_six = #{reserveSix}


		ORDER BY RAND()  limit #{total}
